<template>
  <div class="indexMap">


    <map class="map"
         @bindtap="bindtap"
         @bindregionchange="bindregionchange"
         :latitude="latitude"
         :markers="markers"
         :circles="circles"
         :longitude="longitude"></map>
  </div>
</template>
<script>
  import http from '../../http'
 var that= null;
  export default {
    "name": 'indexMap',
    data(){
      return {
        "longitude": 116.38,
        "latitude": 39.90,
        markers: [],
        circles:[]
      }
    },
    methods:{
      bindtap(e){
        console.log(e);
      },
      bindregionchange(e){
        console.log(e);

      }
    },
    onShow () {
      that= this;
      wx.getStorage({
        key: 'TOKEN',
        success: function(res) {
          console.log("登录成功跳转 获得车辆列表");
          http.getCarList( (res) => {
            if(res.code==0){
              let circles= [];
              let markers= [];
              for( var car of res.data){
                markers.push({
                  id: car.id,
                  "longitude": car.rolLongitude,
                  "latitude":car.rolLatitude,
                  iconPath: 'data:image/png;base64,',
                  name: car.rollatorName
                })

                circles.push(
                  {
                    "longitude": car.rolLongitude,
                    "latitude": car.rolLatitude,
                    "radius": car.radius*1000,
                    'color': '#13ec2b82',
                    'fillColor':'#13ec2b21',
                  })

              }
              that.circles=circles;
              that.markers= markers;

              that.longitude= res.data[0]['rolLongitude']
              this.latitude= res.data[0]['rolLatitude']
            }})
        },
        fail: (res) => {
          console.log("登录失败");
          console.log(res);
          wx.getLocation({
            type: 'wgs84',
            success:  (res) => {
              wx.setStorageSync('unplace', false);
              that.longitude=  res.longitude;
              that.latitude= res.latitude;
              that.markers=[
                {
                  id: 0,
                  longitude: res.longitude,
                  latitude: res.latitude,
                  iconPath: 'data:image/png;base64,',
                  name: '我的位置'
                }
              ];
              that.circles=[{
                "longitude":  res.longitude,
                "latitude": res.latitude,
                "radius": 100,
                'color': '#13ec2b82',
                'fillColor':'#13ec2b21',
              }]

            },
            fail: function (res) {
              wx.setStorageSync('unplace', true);
            }
          });
        }
      })
    },
    mounted(){
      that= this;
      wx.getStorage({
        key: 'TOKEN',
        success: function(res) {
          console.log("登录成功跳转 获得车辆列表");
          http.getCarList( (res) => {
            if(res.code==0){
              let circles= [];
              let markers= [];
              for( var car of res.data){
                markers.push({
                  id: car.id,
                  "longitude": car.rolLongitude,
                  "latitude":car.rolLatitude,
                   iconPath: 'data:image/png;base64,',
                  name: car.rollatorName
                })

                circles.push(
                  {
                    "longitude": car.rolLongitude,
                    "latitude": car.rolLatitude,
                    "radius": car.radius*1000,
                    'color': '#13ec2b82',
                    'fillColor':'#13ec2b21',
                  })

              }
              that.circles=circles;
              that.markers= markers;

              that.longitude= res.data[0]['rolLongitude']
              this.latitude= res.data[0]['rolLatitude']
            }})
        },
        fail: (res) => {
          console.log("登录失败");
          console.log(res);
          wx.getLocation({
            type: 'wgs84',
            success:  (res) => {
              wx.setStorageSync('unplace', false);
              that.longitude=  res.longitude;
              that.latitude= res.latitude;
              that.markers=[
                {
                  id: 0,
                  longitude: res.longitude,
                  latitude: res.latitude,
                  iconPath: 'data:image/png;base64,',
                  name: '我的位置'
                }
              ];
              that.circles=[{
                "longitude":  res.longitude,
                "latitude": res.latitude,
                "radius": 100,
                'color': '#13ec2b82',
                'fillColor':'#13ec2b21',
              }]

            },
            fail: function (res) {
              wx.setStorageSync('unplace', true);
            }
          });
        }
      })
    }
  }
</script>
<style>
  .indexMap{
    width: 100%;
    height: 90%;
    margin-bottom: 60px;
  }
  .indexMap .map{
    height: 100%;
    width: 100%;
  }
</style>
