      </cover-view>
<cover-view class="lB" @click="moveTocenter">
  <!--src="data:image/png;base64,"-->
  <cover-image  class="img"
                src="https://app.ymai.net.cn/static/APPimage/local.png"


  ></cover-image>

</cover-view>

    </map>
