      }
    },
    methods:{
      moveTocenter(){
        console.log("moveTocenter");
        this.mapCtx.moveToLocation()
      },
      setCenter(){
        this.mapCtx.getCenterLocation({
          success: (res) => {

            console.log(res);

            this.car= JSON.parse(this.car)
            this.car.rolLongitude=res.longitude
            this.car.rolLatitude=res.latitude;
            console.log(res);
            var url = `../carInfo/main?car=`+JSON.stringify(this.car)
            wx.redirectTo({ url });
            wx.setNavigationBarTitle({
              title: '编辑'
            });
          }})

      },
      touchend(){
        console.log("touchend");
        this.mapCtx.getCenterLocation({
