              // this.mapCtx.translateMarker({
              //   markerId: 1,
              //   autoRotate: false,
              //   duration: 0,
              //   destination: {
              //     latitude:  res.latitude,
              //     longitude: res.longitude,
              //   },
              //   animationEnd() {
              //     console.log('animation end')
              //   }
              // })

            }})
      },
      regionChangeBegin(e){
        this.check= true;

      },
      regionChangeEnd(e){
        console.log("12end");

      },
      bindtap(e){
        console.log("bindtap");

      },
      bindregionchange(e){
        console.log("bindregionchange");
      }
    },
    onShow(){


        wx.setNavigationBarTitle({
          title: '设置中心'
        });


      this.mapCtx = wx.createMapContext('myMap');
      this.check= false;
      if(this.$mp.query.car){
        let car = JSON.parse(this.$mp.query.car);
        this.car= car;
        this.longitude= car.rolLongitude;
        this.latitude= car.rolLatitude;

        console.log(car);
        console.log("^car^");
        if(this.longitude==''){
          wx.getLocation({
            type: 'gcj02',
            success:  (res) => {
              this.longitude=res.longitude;
              this.latitude=res.latitude;
              console.log(this.longitude);
              console.log("^getLocation^");
              this.mapCtx.moveToLocation();
            }
          })
        }

      }
      if(this.$mp.query.latitude){
        this.latitude= this.$mp.query.latitude;
        console.log(this.latitude);
        console.log("^query lati^");

      }
      if(this.$mp.query.longitude){
        this.longitude= this.$mp.query.longitude;
        console.log(this.longitude);
        console.log("^query long^");
      }

       this.markers[0]={
